pub enum ActionsTree {
    Accept,
    SimpleTransfer {
        to: AccountAddress,
        amount: Amount,
    },
    Send {
        to: ContractAddress,
        receive_name: OwnedReceiveName,
        amount: Amount,
        parameter: Vec<u8>,
    },
    AndThen {
        left: Box<ActionsTree>,
        right: Box<ActionsTree>,
    },
    OrElse {
        left: Box<ActionsTree>,
        right: Box<ActionsTree>,
    },
}
